import React, {Component} from "react";
import CategoryList from "./CategoryList";
import CategoryDescription from "./CategoryDescription";
import {useState} from 'react';

function App() {
    const [ currentCategory, categories, setCurrentCategory, setCategories] = useState([]);

    fetch('http://localhost:8000/categories')
        .then(res => res.json().then(data => {
            console.log(data.categories)
            setCategories(data.categories)
            setCurrentCategory(data.categories[0])
        }))
        const handleChangeCategory = (e) => {
            setCurrentCategory(e.target.value)
        }

    // console.log(categories);

    return (
        <div className="App">
            <h1>Meal-Finder</h1>
            <h3>Select a Category:</h3>
            <select value={currentCategory} onChange={handleChangeCategory} id={"selectCategory"}><CategoryList categories={categories}/></select>
            <CategoryDescription category={currentCategory}/>
        </div>
    );

}

export default App;
